CREATE TABLE ConcertAttendance (
    concert_id INT PRIMARY KEY,
    concert_date DATE,
    venue VARCHAR(255),
    attendance_count INT
);

CREATE TABLE concert_attendance (
    concert_id INT PRIMARY KEY,
    date DATE,
    location VARCHAR(255),
    attendance_count INT
);

