CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE permissions (
    permission_id INT PRIMARY KEY,
    user_id INT,
    permission_type VARCHAR(50) NOT NULL,
    FOREIGN KEY (user_id) REFERENCES users(user_id)
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE user_roles (
    id INT PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE permissions (
    id INT PRIMARY KEY,
    role_id INT NOT NULL,
    permission_name VARCHAR(50) NOT NULL,
    FOREIGN KEY (role_id) REFERENCES user_roles(id)
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE user_roles (
    user_role_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    role_name VARCHAR(50) NOT NULL
);

CREATE TABLE user_permissions (
    permission_id UUID PRIMARY KEY,
    user_role_id UUID REFERENCES user_roles(user_role_id),
    permission_name VARCHAR(50) NOT NULL
);

CREATE TABLE digital_art (
    art_id UUID PRIMARY KEY,
    title VARCHAR(100) NOT NULL,
    artist_id UUID REFERENCES users(user_id),
    art_description TEXT
);

CREATE TABLE art_comments (
    comment_id UUID PRIMARY KEY,
    art_id UUID REFERENCES digital_art(art_id),
    user_id UUID REFERENCES users(user_id),
    comment_text TEXT NOT NULL
);
