tables.table_schema:
CREATE TABLE three_d_models (
    model_id UUID PRIMARY KEY,
    model_name VARCHAR(50) NOT NULL,
    creation_date DATE,
    model_file BYTEA,
    user_id UUID
);

CREATE TABLE textures (
    texture_id UUID PRIMARY KEY,
    texture_name VARCHAR(50) NOT NULL,
    texture_file BYTEA,
    model_id UUID
);

CREATE TABLE 3d_models (
    id INT PRIMARY KEY,
    model_name VARCHAR(255),
    model_file_path VARCHAR(255),
    upload_date TIMESTAMP
);

CREATE TABLE textures (
    id INT PRIMARY KEY,
    texture_name VARCHAR(255),
    texture_file_path VARCHAR(255),
    resolution VARCHAR(50)
);

CREATE TABLE user_interactions (
    id INT PRIMARY KEY,
    user_id INT,
    interaction_type VARCHAR(50),
    interaction_date TIMESTAMP
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    registration_date TIMESTAMP
);

CREATE TABLE Models (
    model_id UUID PRIMARY KEY,
    model_name TEXT NOT NULL,
    model_file BYTEA NOT NULL,
    upload_date TIMESTAMP NOT NULL
);

CREATE TABLE Textures (
    texture_id UUID PRIMARY KEY,
    texture_name TEXT NOT NULL,
    texture_file BYTEA NOT NULL,
    upload_date TIMESTAMP NOT NULL
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    username TEXT NOT NULL,
    email TEXT NOT NULL,
    signup_date TIMESTAMP NOT NULL
);

CREATE TABLE ArtPlatforms (
    platform_id UUID PRIMARY KEY,
    platform_name TEXT NOT NULL,
    platform_url TEXT NOT NULL
);
