tables.table_schema:
CREATE TABLE art_restoration_projects (
    project_id SERIAL PRIMARY KEY,
    project_name VARCHAR(50) NOT NULL,
    restoration_notes TEXT,
    historical_context TEXT
);

CREATE TABLE restoration_steps (
    step_id SERIAL PRIMARY KEY,
    project_id INT REFERENCES art_restoration_projects(project_id),
    step_description TEXT,
    step_date DATE
);

CREATE TABLE RestorationProcesses (
    process_id INT PRIMARY KEY,
    project_id INT,
    process_description TEXT,
    process_date DATE
);

CREATE TABLE ConservationNotes (
    note_id INT PRIMARY KEY,
    project_id INT,
    note TEXT,
    note_date DATE
);

CREATE TABLE HistoricalContext (
    context_id INT PRIMARY KEY,
    project_id INT,
    context_description TEXT
);

CREATE TABLE Projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    project_start_date DATE,
    project_end_date DATE
);
