tables.table_schema:
CREATE TABLE Participants (
  participant_id INT PRIMARY KEY,
  participant_name VARCHAR(50),
  participant_age INT,
  participant_gender VARCHAR(10)
);

CREATE TABLE participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR,
    participant_location VARCHAR,
    participant_age INT
);

CREATE TABLE judges (
    judge_id INT PRIMARY KEY,
    judge_name VARCHAR,
    judge_location VARCHAR
);

CREATE TABLE judges (
  judge_id SERIAL PRIMARY KEY,
  judge_name VARCHAR NOT NULL,
  judge_gender VARCHAR,
  judge_email VARCHAR
);

CREATE TABLE Singers (
    singer_id INT PRIMARY KEY,
    singer_name VARCHAR(255) NOT NULL,
    age INT,
    country VARCHAR(100)
);

CREATE TABLE Judges (
    judge_id INT PRIMARY KEY,
    judge_name VARCHAR(255) NOT NULL,
    specialization VARCHAR(100)
);

CREATE TABLE Assignments (
    assignment_id INT PRIMARY KEY,
    judge_id INT REFERENCES Judges(judge_id),
    performance_id INT REFERENCES Performances(performance_id)
);

CREATE TABLE Awards (
    award_id INT PRIMARY KEY,
    award_name VARCHAR(255) NOT NULL,
    singer_id INT REFERENCES Singers(singer_id)
);
