CREATE TABLE enrolled_students (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(255),
  class_id INT,
  FOREIGN KEY (class_id) REFERENCES singing_classes(class_id)
);

CREATE TABLE singing_classes (
  class_id UUID PRIMARY KEY,
  class_date DATE,
  class_time TIME,
  location VARCHAR
);

CREATE TABLE instructors (
  instructor_id UUID PRIMARY KEY,
  instructor_name VARCHAR,
  instructor_email VARCHAR,
  instructor_phone VARCHAR
);

CREATE TABLE students (
  student_id UUID PRIMARY KEY,
  student_name VARCHAR,
  student_email VARCHAR,
  student_phone VARCHAR
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL,
    student_email VARCHAR(100) UNIQUE
);

CREATE TABLE attendance (
    attendance_id INT PRIMARY KEY,
    class_id INT REFERENCES singing_classes(class_id),
    student_id INT REFERENCES students(student_id),
    attendance_date DATE
);

CREATE TABLE students (
    student_id SERIAL PRIMARY KEY,
    student_name VARCHAR(100) NOT NULL,
    student_email VARCHAR(100),
    student_phone VARCHAR(15)
);

CREATE TABLE teachers (
    teacher_id SERIAL PRIMARY KEY,
    teacher_name VARCHAR(100) NOT NULL,
    teacher_email VARCHAR(100),
    teacher_phone VARCHAR(15)
);
