tables.table_schema:
CREATE TABLE instruments_on_loan (
    instrument_id INT,
    musician_id INT,
    loan_date DATE,
    return_date DATE
);

CREATE TABLE instruments (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    type VARCHAR(255),
    is_on_loan BOOLEAN
);

CREATE TABLE musicians (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    instrument_id INT,
    FOREIGN KEY (instrument_id) REFERENCES instruments(id)
);
