CREATE TABLE instruments ( 
  instrument_id SERIAL PRIMARY KEY, 
  instrument_name VARCHAR(50) NOT NULL, 
  instrument_type VARCHAR(50) NOT NULL, 
  instrument_value DECIMAL(10,2) NOT NULL 
);

CREATE TABLE instruments (
  instrument_id INT PRIMARY KEY,
  instrument_name VARCHAR(255) NOT NULL,
  value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE insurance_policies (
  policy_id INT PRIMARY KEY,
  policy_holder VARCHAR(255) NOT NULL,
  instrument_id INT NOT NULL,
  FOREIGN KEY (instrument_id) REFERENCES instruments(instrument_id)
);

CREATE TABLE instruments (
   instrument_id INT PRIMARY KEY,
   instrument_name VARCHAR(255),
   instrument_type VARCHAR(50),
   instrument_value DECIMAL(10, 2)
);

CREATE TABLE insurance_policies (
   policy_id INT PRIMARY KEY,
   policy_holder VARCHAR(255),
   policy_type VARCHAR(50),
   policy_amount DECIMAL(10, 2)
);

CREATE TABLE instruments (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    value DECIMAL
);

CREATE TABLE clients (
    client_id INT PRIMARY KEY,
    client_name VARCHAR(50)
);

CREATE TABLE policies (
    policy_id INT PRIMARY KEY,
    policy_number VARCHAR(20),
    client_id INT,
    FOREIGN KEY (client_id) REFERENCES clients(client_id)
);

CREATE TABLE policy_instruments (
    policy_id INT,
    instrument_id INT,
    FOREIGN KEY (policy_id) REFERENCES policies(policy_id),
    FOREIGN KEY (instrument_id) REFERENCES instruments(id)
);

CREATE TABLE instruments (
    id UUID PRIMARY KEY,
    name STRING,
    value DECIMAL
);

CREATE TABLE categories (
    category_id UUID PRIMARY KEY,
    category_name STRING
);

CREATE TABLE instrument_categories (
    instrument_id UUID REFERENCES instruments(id),
    category_id UUID REFERENCES categories(category_id),
    PRIMARY KEY (instrument_id, category_id)
);

CREATE TABLE owners (
    owner_id UUID PRIMARY KEY,
    owner_name STRING
);

CREATE TABLE instrument_owners (
    instrument_id UUID REFERENCES instruments(id),
    owner_id UUID REFERENCES owners(owner_id),
    PRIMARY KEY (instrument_id, owner_id)
);
