tables.table_schema:
CREATE TABLE instrument_repair_recommendations (
  instrument_id INT PRIMARY KEY,
  repair_recommendation TEXT
);

CREATE TABLE instrument_usage_patterns (
  instrument_id INT,
  usage_duration FLOAT,
  last_used_date DATE,
  PRIMARY KEY (instrument_id, last_used_date)
);

CREATE TABLE InstrumentUsage (
    instrument_id INT PRIMARY KEY,
    user_id INT,
    timestamp TIMESTAMP,
    duration_minutes INT
);

CREATE TABLE RepairOptions (
    repair_option_id INT PRIMARY KEY,
    repair_description TEXT,
    estimated_cost DECIMAL(10,2)
);

CREATE TABLE RepairRecommendation (
    recommendation_id INT PRIMARY KEY,
    instrument_id INT,
    repair_option_id INT,
    recommendation_date DATE
);

CREATE TABLE InstrumentUsage (
    instrument_id INT PRIMARY KEY,
    usage_hours INT,
    last_maintenance_date DATE
);

CREATE TABLE RepairHistory (
    repair_id INT PRIMARY KEY,
    instrument_id INT,
    repair_date DATE,
    repair_description TEXT
);

CREATE TABLE RepairRecommendations (
    recommendation_id INT PRIMARY KEY,
    instrument_id INT,
    recommended_service VARCHAR,
    recommended_date DATE
);

CREATE TABLE InstrumentMaster (
    instrument_id INT PRIMARY KEY,
    instrument_name VARCHAR,
    instrument_type VARCHAR
);

CREATE TABLE instrument_analysis ( 
    instrument_id INT PRIMARY KEY, 
    instrument_name VARCHAR(255), 
    usage_hours INT, 
    last_repair_date DATE 
);

CREATE TABLE instrument_users ( 
    user_id INT PRIMARY KEY, 
    user_name VARCHAR(255), 
    instrument_id INT, 
    usage_frequency INT 
);

CREATE TABLE repair_history ( 
    repair_id INT PRIMARY KEY, 
    instrument_id INT, 
    repair_type VARCHAR(255), 
    repair_date DATE 
);

CREATE TABLE repair_recommendations ( 
    recommendation_id INT PRIMARY KEY, 
    instrument_id INT, 
    recommended_repair_type VARCHAR(255), 
    recommendation_date DATE 
);
