tables.table_schema:
CREATE TABLE genres (
    genre_id INT PRIMARY KEY,
    genre_name VARCHAR(50) NOT NULL,
    genre_description TEXT
);

CREATE TABLE genre_characteristics (
    char_id SERIAL PRIMARY KEY,
    genre_id INT REFERENCES genres(genre_id),
    characteristic_name VARCHAR(50) NOT NULL,
    characteristic_value TEXT
);

CREATE TABLE genre (
    genre_id INT PRIMARY KEY,
    genre_name VARCHAR(50) NOT NULL,
    genre_description TEXT
);

CREATE TABLE subgenre (
    subgenre_id INT PRIMARY KEY,
    subgenre_name VARCHAR(50) NOT NULL,
    genre_id INT,
    FOREIGN KEY (genre_id) REFERENCES genre(genre_id)
);

CREATE TABLE characteristics (
    characteristic_id INT PRIMARY KEY,
    characteristic_name VARCHAR(50) NOT NULL,
    subgenre_id INT,
    FOREIGN KEY (subgenre_id) REFERENCES subgenre(subgenre_id)
);

CREATE TABLE genre (
  id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  description TEXT
);

CREATE TABLE artist (
  id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  genre_id INT,
  FOREIGN KEY (genre_id) REFERENCES genre(id)
);

CREATE TABLE Genre (
    genre_id INT PRIMARY KEY,
    genre_name VARCHAR(255) NOT NULL,
    genre_description TEXT
);

CREATE TABLE Artist (
    artist_id INT PRIMARY KEY,
    artist_name VARCHAR(255) NOT NULL,
    artist_genre_id INT,
    FOREIGN KEY (artist_genre_id) REFERENCES Genre(genre_id)
);
