tables.table_schema:
CREATE TABLE composers (
  id SERIAL PRIMARY KEY,
  composer_name STRING
);

CREATE TABLE major_works (
  id SERIAL PRIMARY KEY,
  composer_id INT,
  work_title STRING,
  year_published INT
);

CREATE TABLE Composers (
  composer_id INT PRIMARY KEY,
  composer_name VARCHAR(50) NOT NULL,
  birth_year INT,
  death_year INT
);

CREATE TABLE MajorWorks (
  work_id INT PRIMARY KEY,
  work_title VARCHAR(100) NOT NULL,
  composer_id INT REFERENCES Composers(composer_id),
  release_year INT
);

CREATE TABLE Compositions (
  composition_id INT PRIMARY KEY,
  composition_title VARCHAR(100) NOT NULL,
  work_id INT REFERENCES MajorWorks(work_id),
  genre VARCHAR(50)
);

CREATE TABLE composers (
  composer_id INT PRIMARY KEY,
  composer_name STRING,
  birth_year INT,
  death_year INT
);

CREATE TABLE conductors (
  conductor_id INT PRIMARY KEY,
  conductor_name STRING,
  birth_year INT
);

CREATE TABLE genres (
    genre_id INT PRIMARY KEY,
    genre_name VARCHAR(50) NOT NULL
);

CREATE TABLE periods (
    period_id INT PRIMARY KEY,
    period_name VARCHAR(50) NOT NULL
);
