tables.table_schema:
CREATE TABLE ChordProgressions (
  progression_id SERIAL PRIMARY KEY,
  chord_progression VARCHAR(255) NOT NULL,
  emotion VARCHAR(100) NOT NULL
);

CREATE TABLE ChordProgressions (
  id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  chords TEXT[] NOT NULL
);

CREATE TABLE EmotionalEffects (
  id SERIAL PRIMARY KEY,
  progression_id INT REFERENCES ChordProgressions(id),
  effect TEXT NOT NULL,
  intensity INT
);

CREATE TABLE Emotions (
    id INT PRIMARY KEY,
    emotion_name VARCHAR(50),
    description TEXT
);

CREATE TABLE Users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    registration_date DATE
);

CREATE TABLE Progressions (
  progression_id UUID PRIMARY KEY,
  progression_name VARCHAR(255),
  chord_ids UUID[],
  description TEXT
);
