tables.table_schema:
CREATE TABLE instruments (
  instrument_id INT PRIMARY KEY,
  instrument_name VARCHAR(255),
  popularity_ranking INT
);

CREATE TABLE instruments ( 
    instrument_id INT PRIMARY KEY, 
    instrument_name VARCHAR(50) NOT NULL, 
    popularity_score INT 
);

CREATE TABLE instrument_sales ( 
    sale_id INT PRIMARY KEY, 
    instrument_id INT REFERENCES instruments(instrument_id), 
    sale_date DATE, 
    sale_amount DECIMAL(10,2) 
);

CREATE TABLE popularity_history ( 
    record_id INT PRIMARY KEY, 
    instrument_id INT REFERENCES instruments(instrument_id), 
    month_year DATE, 
    popularity_rank INT 
);

CREATE TABLE InstrumentPopularity ( instrument_id INT, instrument_name VARCHAR(50), popularity_score INT, record_date DATE );

CREATE TABLE InstrumentCategory ( category_id INT, category_name VARCHAR(50) );

CREATE TABLE InstrumentHistory ( history_id INT, instrument_id INT, popularity_score INT, record_date DATE );

CREATE TABLE ArtistInstrument ( artist_id INT, instrument_id INT, play_count INT );

CREATE TABLE InstrumentPopularity (
  instrument_id INT PRIMARY KEY,
  instrument_name VARCHAR(50),
  popularity_score INT
);

CREATE TABLE YearlyPopularity (
  year_id INT PRIMARY KEY,
  year INT
);

CREATE TABLE InstrumentUsage (
  usage_id INT PRIMARY KEY,
  instrument_id INT,
  year_id INT,
  num_users INT
);

CREATE TABLE UserProfiles (
  user_id INT PRIMARY KEY,
  user_name VARCHAR(50)
);

CREATE TABLE InstrumentCategories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50)
);
