tables.table_schema:
CREATE TABLE MusicalScales (
 scale_id INT PRIMARY KEY,
 scale_name VARCHAR(50) NOT NULL,
 scale_notes VARCHAR(255) NOT NULL
);

CREATE TABLE ScaleRelationships (
 relationship_id INT PRIMARY KEY,
 parent_scale_id INT NOT NULL,
 child_scale_id INT NOT NULL,
 relationship_type VARCHAR(50) NOT NULL
);

CREATE TABLE traditional_scales (
    scale_id INT PRIMARY KEY,
    scale_name VARCHAR(50),
    tonic_note VARCHAR(10),
    note_sequence TEXT
);

CREATE TABLE modern_scales (
    scale_id INT PRIMARY KEY,
    scale_name VARCHAR(50),
    mode_type VARCHAR(20),
    note_sequence TEXT
);

CREATE TABLE scale_relationships (
    relationship_id INT PRIMARY KEY,
    scale_id1 INT,
    scale_id2 INT,
    relationship_type VARCHAR(20)
);

CREATE TABLE scale_notes (
    note_id INT PRIMARY KEY,
    scale_id INT,
    note_name VARCHAR(10),
    position_in_scale INT
);

CREATE TABLE traditional_scales (
    scale_id INT PRIMARY KEY,
    scale_name TEXT
);

CREATE TABLE modern_scales (
    scale_id INT PRIMARY KEY,
    scale_name TEXT,
    scale_type TEXT
);

CREATE TABLE scale_relations (
    relation_id INT PRIMARY KEY,
    scale1_id INT,
    scale2_id INT,
    relation_type TEXT
);

CREATE TABLE scale_notes (
    note_id INT PRIMARY KEY,
    note_name TEXT,
    scale_id INT
);

CREATE TABLE scale_properties (
    property_id INT PRIMARY KEY,
    property_name TEXT,
    scale_id INT
);
