CREATE TABLE Lyrics (
    id INT PRIMARY KEY,
    lyric_text TEXT,
    musical_composition_id INT
);

CREATE TABLE MusicalCompositions (
    id INT PRIMARY KEY,
    composition_title VARCHAR(255),
    composer_name VARCHAR(255)
);

CREATE TABLE lyrics (
    id INT PRIMARY KEY,
    artist_id INT,
    lyrics_text TEXT
);

CREATE TABLE artists (
    id INT PRIMARY KEY,
    artist_name VARCHAR(255),
    genre VARCHAR(50)
);

CREATE TABLE compositions (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    artist_id INT
);

CREATE TABLE Lyrics (
    lyric_id INT PRIMARY KEY,
    lyric_text TEXT,
    composer_id INT,
    FOREIGN KEY (composer_id) REFERENCES Composers(composer_id)
);

CREATE TABLE Composers (
    composer_id INT PRIMARY KEY,
    composer_name TEXT
);
