tables.table_schema:
CREATE TABLE InfluenceTracking (
    era_id INT PRIMARY KEY,
    era_name VARCHAR(50),
    influence_description TEXT
);

CREATE TABLE Musical_Eras (
    era_id INT PRIMARY KEY,
    era_name VARCHAR(50) NOT NULL
);

CREATE TABLE Music_Compositions (
    composition_id INT PRIMARY KEY,
    composition_title VARCHAR(100) NOT NULL,
    era_id INT REFERENCES Musical_Eras(era_id)
);

CREATE TABLE music_eras (
    era_id INT PRIMARY KEY,
    era_name VARCHAR(50) NOT NULL
);

CREATE TABLE music_compositions (
    composition_id INT PRIMARY KEY,
    composition_name VARCHAR(100) NOT NULL,
    era_id INT,
    FOREIGN KEY (era_id) REFERENCES music_eras(era_id)
);

CREATE TABLE influences (
    influence_id INT PRIMARY KEY,
    composition_id INT,
    influence_description TEXT NOT NULL,
    FOREIGN KEY (composition_id) REFERENCES music_compositions(composition_id)
);

CREATE TABLE Era (
  era_id INT PRIMARY KEY,
  era_name TEXT
);

CREATE TABLE Composer (
  composer_id INT PRIMARY KEY,
  composer_name TEXT,
  era_id INT,
  FOREIGN KEY (era_id) REFERENCES Era(era_id)
);

CREATE TABLE Composition (
  composition_id INT PRIMARY KEY,
  composition_name TEXT,
  composer_id INT,
  FOREIGN KEY (composer_id) REFERENCES Composer(composer_id)
);

CREATE TABLE Genre (
  genre_id INT PRIMARY KEY,
  genre_name TEXT
);
