tables.table_schema:
CREATE TABLE concert_schedule (
    concert_id INT PRIMARY KEY,
    artist_name VARCHAR(50) NOT NULL,
    venue_name VARCHAR(100) NOT NULL,
    concert_date DATE NOT NULL
);

CREATE TABLE musicians (
    musician_id INT PRIMARY KEY,
    musician_name VARCHAR(255) NOT NULL,
    musician_genre VARCHAR(100) NOT NULL
);

CREATE TABLE musicians (
    musician_id INT PRIMARY KEY,
    musician_name VARCHAR(50) NOT NULL,
    birthdate DATE,
    nationality VARCHAR(50)
);

CREATE TABLE artists (
    artist_id UUID PRIMARY KEY,
    artist_name VARCHAR(255),
    genre VARCHAR(50),
    agency VARCHAR(100)
);

CREATE TABLE albums (
    album_id UUID PRIMARY KEY,
    album_title VARCHAR(255),
    release_date DATE,
    artist_id UUID REFERENCES artists(artist_id)
);

CREATE TABLE venues (
    venue_id UUID PRIMARY KEY,
    venue_name VARCHAR(255),
    location VARCHAR(100),
    capacity INT
);

CREATE TABLE performances (
    performance_id UUID PRIMARY KEY,
    date_time TIMESTAMP,
    venue_id UUID REFERENCES venues(venue_id),
    tour_id UUID REFERENCES tours(tour_id)
);
