tables.table_schema:
CREATE TABLE musicians (
    musician_id UUID PRIMARY KEY,
    musician_name VARCHAR(255) NOT NULL,
    genre VARCHAR(100) NOT NULL
);

CREATE TABLE awards (
    award_id UUID PRIMARY KEY,
    award_name VARCHAR(255) NOT NULL,
    award_year INT NOT NULL,
    musician_id UUID REFERENCES musicians(musician_id)
);

CREATE TABLE nominations (
    nomination_id UUID PRIMARY KEY,
    nomination_name VARCHAR(255) NOT NULL,
    nomination_year INT NOT NULL,
    musician_id UUID REFERENCES musicians(musician_id)
);

CREATE TABLE musician_awards ( 
    musician_id UUID, 
    award_name TEXT, 
    nomination_year INT, 
    PRIMARY KEY (musician_id, award_name) 
);

CREATE TABLE musician_albums ( 
    musician_id UUID, 
    album_name TEXT, 
    release_date DATE, 
    PRIMARY KEY (musician_id, album_name) 
);

CREATE TABLE award_categories ( 
    category_id UUID, 
    category_name TEXT, 
    PRIMARY KEY (category_id) 
);

CREATE TABLE musician_genres ( 
    musician_id UUID, 
    genre_name TEXT, 
    PRIMARY KEY (musician_id, genre_name) 
);

CREATE TABLE artist ( 
    artist_id INT PRIMARY KEY, 
    artist_name VARCHAR(50) NOT NULL, 
    birth_date DATE, 
    genre VARCHAR(30) 
);

CREATE TABLE award ( 
    award_id INT PRIMARY KEY, 
    award_name VARCHAR(50) NOT NULL, 
    category VARCHAR(50), 
    year INT 
);

CREATE TABLE committee ( 
    committee_id INT PRIMARY KEY, 
    committee_name VARCHAR(50) NOT NULL, 
    category VARCHAR(50) 
);
