tables.table_schema:
CREATE TABLE musicians (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  genre VARCHAR(50)
);

CREATE TABLE music_stats (
  id INT PRIMARY KEY,
  musician_id INT,
  stream_count INT,
  download_count INT
);

CREATE TABLE musicians (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  genre VARCHAR(100)
);

CREATE TABLE statistics (
  id INT PRIMARY KEY,
  song_id INT,
  stream_count INT,
  download_count INT
);

CREATE TABLE Musicians (
    musician_id INT PRIMARY KEY,
    musician_name TEXT NOT NULL,
    genre TEXT
);

CREATE TABLE Songs (
    song_id INT PRIMARY KEY,
    song_name TEXT NOT NULL,
    release_date DATE,
    musician_id INT REFERENCES Musicians (musician_id)
);

CREATE TABLE Streams (
    stream_id INT PRIMARY KEY,
    song_id INT REFERENCES Songs (song_id),
    stream_date DATE,
    num_streams INT
);

CREATE TABLE Downloads (
    download_id INT PRIMARY KEY,
    song_id INT REFERENCES Songs (song_id),
    download_date DATE,
    device_type TEXT
);

CREATE TABLE Albums (
    album_id INT PRIMARY KEY,
    album_name TEXT NOT NULL,
    release_date DATE,
    musician_id INT REFERENCES Musicians (musician_id)
);
