	email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE Genres (
	genre_id INT PRIMARY KEY,
	genre_name VARCHAR(50) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE genres (
    genre_id INT PRIMARY KEY,
    genre_name VARCHAR(255)
);

CREATE TABLE songs (
    song_id INT PRIMARY KEY,
    song_name VARCHAR(255),
    genre_id INT,
    FOREIGN KEY (genre_id) REFERENCES genres(genre_id)
);

CREATE TABLE users (
  user_id UUID PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(100) NOT NULL
);

CREATE TABLE genres (
  genre_id UUID PRIMARY KEY,
  genre_name VARCHAR(50) NOT NULL
);

CREATE TABLE songs (
  song_id UUID PRIMARY KEY,
  song_name VARCHAR(100) NOT NULL,
  genre_id UUID REFERENCES genres(genre_id)
);

CREATE TABLE user_listening_history (
  user_id UUID REFERENCES users(user_id),
  song_id UUID REFERENCES songs(song_id),
  listening_date TIMESTAMP,
  PRIMARY KEY (user_id, song_id)
);

CREATE TABLE recommendations (
  recommendation_id UUID PRIMARY KEY,
  user_id UUID REFERENCES users(user_id),
  recommended_song_id UUID REFERENCES songs(song_id)
);
