CREATE TABLE regions (
	region_id INT PRIMARY KEY,
	region_name TEXT
);

CREATE TABLE genres (
	genre_id INT PRIMARY KEY,
	genre_name TEXT
);

CREATE TABLE songs (
	song_id INT PRIMARY KEY,
	song_title TEXT,
