tables.table_schema:
CREATE TABLE concert_setlist (
    concert_id INT PRIMARY KEY,
    setlist TEXT
);

CREATE TABLE concert_details (
    concert_id INT PRIMARY KEY,
    attendance INT,
    review TEXT
);

CREATE TABLE Setlist (
    concert_id UUID,
    song_name VARCHAR(50),
    song_order INT,
    PRIMARY KEY (concert_id, song_order)
);

CREATE TABLE Attendance (
    concert_id UUID,
    attendee_name VARCHAR(50),
    attendance_status VARCHAR(10),
    PRIMARY KEY (concert_id, attendee_name)
);

CREATE TABLE setlists (
  setlist_id UUID NOT NULL PRIMARY KEY,
  concert_date TIMESTAMP NOT NULL,
  songs TEXT[] NOT NULL
);

CREATE TABLE attendance (
  attendance_id UUID NOT NULL PRIMARY KEY,
  concert_date TIMESTAMP NOT NULL,
  total_attendees INT NOT NULL
);

CREATE TABLE concerts (
  concert_id UUID NOT NULL PRIMARY KEY,
  concert_date TIMESTAMP NOT NULL,
  venue TEXT NOT NULL,
  artist TEXT NOT NULL
);

CREATE TABLE Venues ( 
 venue_id UUID PRIMARY KEY, 
 venue_name VARCHAR(255), 
 city VARCHAR(100), 
 capacity INT 
 );
