tables.table_schema:
CREATE TABLE Concert_Attendees (
    attendee_id INT PRIMARY KEY,
    attendee_name VARCHAR(255),
    concert_name VARCHAR(255),
    feedback_score INT
);

CREATE TABLE concert_attendees (
    attendee_id INT PRIMARY KEY,
    attendee_name VARCHAR,
    feedback TEXT,
    survey_response VARCHAR
);

CREATE TABLE concerts (
    concert_id INT PRIMARY KEY,
    concert_name VARCHAR,
    concert_date DATE,
    venue_name VARCHAR
);
