tables.table_schema:
CREATE TABLE singers_progress (
    singer_id INT PRIMARY KEY,
    name VARCHAR(50),
    progress INT,
    achievements TEXT
);

CREATE TABLE singers (
    singer_id INT PRIMARY KEY,
    singer_name VARCHAR(255),
    start_date DATE,
    performance_rating INT
);

CREATE TABLE singers_progress (
	singer_id INT PRIMARY KEY,
	name VARCHAR(50) NOT NULL,
	performance_score INT,
	review_date DATE
);

CREATE TABLE performance_review (
	review_id INT PRIMARY KEY,
	singer_id INT,
	review_date DATE,
	feedback TEXT
);

CREATE TABLE achievements (
	achievement_id INT PRIMARY KEY,
	singer_id INT,
	achievement_title VARCHAR(100),
	date_achieved DATE
);

CREATE TABLE SingersProgress (
    singer_id SERIAL PRIMARY KEY,
    singer_name VARCHAR(50) NOT NULL,
    progress_report TEXT,
    performance_rating INT
);

CREATE TABLE Singers (
    singer_id INT PRIMARY KEY,
    singer_name VARCHAR(50),
    birthdate DATE,
    joined_date DATE
);

CREATE TABLE Songs (
    song_id INT PRIMARY KEY,
    song_title VARCHAR(100),
    release_date DATE,
    genre VARCHAR(50)
);

CREATE TABLE Achievements (
    achievement_id INT PRIMARY KEY,
    achievement_description TEXT,
    date_achieved DATE,
    singer_id INT
);
