tables.table_schema:
CREATE TABLE user_preferences (
user_id INT PRIMARY KEY,
name VARCHAR,
email VARCHAR,
preferred_genres VARCHAR
);

CREATE TABLE listening_habits (
user_id INT PRIMARY KEY,
last_login TIMESTAMP,
average_listen_time INT,
favorite_artist VARCHAR
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    registration_date DATE
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    birthdate DATE
);

CREATE TABLE artists (
    artist_id UUID PRIMARY KEY,
    artist_name VARCHAR(50),
    genre VARCHAR(50),
    popularity INT
);
