tables.table_schema:
CREATE TABLE therapist (
    therapist_id INT PRIMARY KEY,
    therapist_name VARCHAR(100) NOT NULL,
    therapist_specialty VARCHAR(100) NOT NULL,
    therapist_availability TIME[]
);

CREATE TABLE patient (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(100) NOT NULL,
    patient_age INT,
    patient_contact VARCHAR(20)
);

CREATE TABLE appointment (
    appointment_id INT PRIMARY KEY,
    session_id INT REFERENCES session(session_id),
    appointment_status VARCHAR(50) NOT NULL,
    appointment_notes TEXT
);

CREATE TABLE preference (
    preference_id INT PRIMARY KEY,
    patient_id INT REFERENCES patient(patient_id),
    therapist_specialty_preference VARCHAR(100) NOT NULL,
    appointment_time_preference TIME
);
