tables.table_schema:
CREATE TABLE music_therapy_techniques (
    technique_id INT PRIMARY KEY,
    technique_name VARCHAR(50) NOT NULL,
    technique_description TEXT
);

CREATE TABLE Therapy_Techniques (
    technique_id INT PRIMARY KEY,
    technique_name TEXT,
    description TEXT
);

CREATE TABLE Therapy_Exercises (
    exercise_id INT PRIMARY KEY,
    exercise_name TEXT,
    duration INT,
    difficulty_level TEXT
);

CREATE TABLE Therapy_Activities (
    activity_id INT PRIMARY KEY,
    activity_name TEXT,
    category TEXT
);

CREATE TABLE Techniques (
  technique_id INT PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  description TEXT
);

CREATE TABLE Exercises (
  exercise_id INT PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  duration_minutes INT
);

CREATE TABLE Activities (
  activity_id INT PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  type VARCHAR(50),
  duration_minutes INT
);

CREATE TABLE Sessions (
  session_id INT PRIMARY KEY,
  plan_id INT,
  date_time DATETIME,
  notes TEXT
);
