tables.table_schema:
CREATE TABLE therapist_qualifications (
    therapist_id INT PRIMARY KEY,
    qualification_name VARCHAR(255),
    certification_status BOOLEAN
);

CREATE TABLE therapist_certifications (
    certification_id INT PRIMARY KEY,
    therapist_id INT,
    certification_name VARCHAR(255),
    certification_date DATE
);

CREATE TABLE therapist_training (
    training_id INT PRIMARY KEY,
    therapist_id INT,
    training_name VARCHAR(255),
    training_duration INT
);

CREATE TABLE therapist_standards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(255),
    required_certification BOOLEAN,
    required_training BOOLEAN
);

CREATE TABLE qualifications (
    qualification_id INT PRIMARY KEY,
    qualification_name VARCHAR(255) NOT NULL,
    related_field VARCHAR(100),
    valid_duration INT
);

CREATE TABLE certifications (
    certification_id INT PRIMARY KEY,
    certification_name VARCHAR(255) NOT NULL,
    issuing_authority VARCHAR(100),
    valid_until DATE
);

CREATE TABLE training_sessions (
    session_id INT PRIMARY KEY,
    session_title VARCHAR(255) NOT NULL,
    trainer_name VARCHAR(100),
    duration_hours INT
);

CREATE TABLE therapist_training (
    therapist_id INT,
    session_id INT,
    CONSTRAINT fk_therapist_id FOREIGN KEY (therapist_id) REFERENCES music_therapists(therapist_id),
    CONSTRAINT fk_session_id FOREIGN KEY (session_id) REFERENCES training_sessions(session_id)
);

CREATE TABLE therapist_certifications (
    therapist_id INT,
    certification_id INT,
    CONSTRAINT fk_therapist_id FOREIGN KEY (therapist_id) REFERENCES music_therapists(therapist_id),
    CONSTRAINT fk_certification_id FOREIGN KEY (certification_id) REFERENCES certifications(certification_id)
);
