tables.table_schema:
CREATE TABLE MusicTeachers (
    ID INT PRIMARY KEY,
    Name VARCHAR(50),
    Email VARCHAR(50),
    Phone VARCHAR(15)
);

CREATE TABLE MusicTeachers (
  teacher_id INT PRIMARY KEY,
  teacher_name TEXT,
  teaching_subject TEXT
);

CREATE TABLE TeachingCalendar (
  teacher_id INT,
  teaching_date DATE,
  available_slots INT
);
