CREATE TABLE music_classes (
    class_id INT PRIMARY KEY,
    class_name VARCHAR(255),
    class_instructor VARCHAR(255),
    class_capacity INT
);

CREATE TABLE music_classes (
    class_id INT PRIMARY KEY,
    class_name VARCHAR(50) NOT NULL,
    class_instructor VARCHAR(50) NOT NULL,
    class_schedule TIMESTAMP
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL,
    student_email VARCHAR(50) UNIQUE,
    student_phone VARCHAR(15)
);

CREATE TABLE music_schools (
  school_id INT PRIMARY KEY,
  school_name VARCHAR(50)
);

CREATE TABLE music_classes (
  class_id INT PRIMARY KEY,
  class_name VARCHAR(50),
  school_id INT,
  FOREIGN KEY (school_id) REFERENCES music_schools(school_id)
);

CREATE TABLE students (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(50),
  class_id INT,
  FOREIGN KEY (class_id) REFERENCES music_classes(class_id)
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50),
    instrument VARCHAR(50),
    grade_level INT
);

CREATE TABLE classes (
    class_id INT PRIMARY KEY,
    class_name VARCHAR(50),
    teacher VARCHAR(50),
    schedule VARCHAR(50)
);

CREATE TABLE events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50),
    event_date DATE,
    location VARCHAR(50)
);

CREATE TABLE grades (
    grade_id INT PRIMARY KEY,
    student_id INT,
    class_id INT,
    grade DECIMAL(3, 2)
);
