tables.table_schema:
CREATE TABLE music_repertoire ( 
    music_piece_id INT PRIMARY KEY, 
    music_piece_title VARCHAR(255), 
    composer VARCHAR(255), 
    student_id INT 
);

CREATE TABLE student_compositions ( 
    composition_id INT PRIMARY KEY, 
    composition_title VARCHAR(255), 
    composer VARCHAR(255), 
    student_id INT 
);

CREATE TABLE Students (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(255) NOT NULL,
  study_program VARCHAR(100) NOT NULL
);

CREATE TABLE Compositions (
  composition_id INT PRIMARY KEY,
  composer_name VARCHAR(255) NOT NULL,
  title VARCHAR(255) NOT NULL,
  genre VARCHAR(100)
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(255) NOT NULL,
    birthdate DATE,
    level VARCHAR(50)
);

CREATE TABLE compositions (
    composition_id INT PRIMARY KEY,
    composition_title VARCHAR(255) NOT NULL,
    composer VARCHAR(255),
    year_composed INT
);

CREATE TABLE repertoire (
    repertoire_id INT PRIMARY KEY,
    student_id INT,
    composition_id INT,
    date_studied DATE,
    FOREIGN KEY (student_id) REFERENCES students(student_id),
    FOREIGN KEY (composition_id) REFERENCES compositions(composition_id)
);

CREATE TABLE genres (
    genre_id INT PRIMARY KEY,
    genre_name VARCHAR(100) NOT NULL
);

CREATE TABLE composition_genre (
    composition_id INT,
    genre_id INT,
    PRIMARY KEY (composition_id, genre_id),
    FOREIGN KEY (composition_id) REFERENCES compositions(composition_id),
    FOREIGN KEY (genre_id) REFERENCES genres(genre_id)
);
