tables.table_schema:
CREATE TABLE music_workshops (
  workshop_id INT PRIMARY KEY,
  workshop_name VARCHAR(255),
  workshop_date DATE,
  workshop_location VARCHAR(255)
);

CREATE TABLE GuestLectures (
  lecture_id INT PRIMARY KEY,
  lecture_topic VARCHAR(255) NOT NULL,
  lecture_date DATE,
  lecture_speaker VARCHAR(255) NOT NULL
);

CREATE TABLE Participants (
  participant_id INT PRIMARY KEY,
  participant_name VARCHAR(255) NOT NULL,
  participant_email VARCHAR(255),
  participant_type VARCHAR(50)
);

CREATE TABLE attendees (
    attendee_id SERIAL PRIMARY KEY,
    attendee_name VARCHAR(100) NOT NULL,
    attendee_email VARCHAR(100),
    attendee_type VARCHAR(50)
);

CREATE TABLE workshop_attendees (
    workshop_id INT REFERENCES music_workshops(workshop_id),
    attendee_id INT REFERENCES attendees(attendee_id),
    PRIMARY KEY (workshop_id, attendee_id)
);
