tables.table_schema:
CREATE TABLE festival_lineup (
    event_id UUID PRIMARY KEY,
    artist_name TEXT,
    performance_time TIMESTAMP,
    stage_name TEXT
);

CREATE TABLE festival_event_lineup (
  event_id INT PRIMARY KEY,
  artist_name VARCHAR(255),
  performance_time TIMESTAMP,
  stage_name VARCHAR(255)
);

CREATE TABLE festival_stage (
  stage_id INT PRIMARY KEY,
  stage_name VARCHAR(255),
  location VARCHAR(255)
);

CREATE TABLE artists (
  artist_id UUID PRIMARY KEY,
  artist_name VARCHAR(255) NOT NULL,
  genre VARCHAR(100) NOT NULL
);

CREATE TABLE performance_times (
  performance_id UUID PRIMARY KEY,
  start_time TIMESTAMP NOT NULL,
  end_time TIMESTAMP NOT NULL
);

CREATE TABLE stages (
  stage_id UUID PRIMARY KEY,
  stage_name VARCHAR(100) NOT NULL
);

CREATE TABLE artists (
    artist_id UUID PRIMARY KEY,
    artist_name VARCHAR(255) NOT NULL,
    genre VARCHAR(50) NOT NULL
);

CREATE TABLE stages (
    stage_id UUID PRIMARY KEY,
    stage_name VARCHAR(100) NOT NULL,
    capacity INT NOT NULL
);

CREATE TABLE events (
    event_id UUID PRIMARY KEY,
    event_name VARCHAR(255) NOT NULL,
    event_date DATE NOT NULL
);

CREATE TABLE performances (
    performance_id UUID PRIMARY KEY,
    event_id UUID NOT NULL,
    artist_id UUID NOT NULL,
    stage_id UUID NOT NULL,
    performance_time TIMESTAMP NOT NULL
);

CREATE TABLE festival (
    festival_id SERIAL PRIMARY KEY,
    festival_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE artist (
    artist_id SERIAL PRIMARY KEY,
    artist_name VARCHAR(255) NOT NULL,
    genre VARCHAR(255)
);

CREATE TABLE stage (
    stage_id SERIAL PRIMARY KEY,
    stage_name VARCHAR(255) NOT NULL
);

CREATE TABLE ticket (
    ticket_id SERIAL PRIMARY KEY,
    lineup_id INT REFERENCES lineup(lineup_id),
    price DECIMAL(10, 2) NOT NULL
);
