tables.table_schema:
CREATE TABLE SponsorshipDeals (
 id INT PRIMARY KEY,
 brand_name VARCHAR(255),
 festival_name VARCHAR(255),
 deal_amount DECIMAL
);

CREATE TABLE BrandAgreements (
 id INT PRIMARY KEY,
 brand_name VARCHAR(255),
 agreement_type VARCHAR(50),
 agreement_date DATETIME
);

CREATE TABLE Brands (
    brand_id INT PRIMARY KEY,
    brand_name VARCHAR(50) NOT NULL,
    industry VARCHAR(50)
);

CREATE TABLE Deal_Terms (
    term_id INT PRIMARY KEY,
    deal_id INT,
    term_description TEXT,
    term_value DECIMAL(10, 2)
);
