tables.table_schema:
CREATE TABLE festival_attendance (
    festival_id INT PRIMARY KEY,
    festival_name VARCHAR(255),
    attendance_count INT,
    date_attended DATE
);
CREATE TABLE marketing_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    target_audience VARCHAR(255),
    effectiveness_score FLOAT
);

CREATE TABLE festival_attendance (
    id INT PRIMARY KEY,
    festival_name VARCHAR(255) NOT NULL,
    attendance_count INT NOT NULL,
    date_attended DATE NOT NULL
);

CREATE TABLE festival_locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255) NOT NULL,
    city VARCHAR(255) NOT NULL,
    country VARCHAR(255) NOT NULL
);

CREATE TABLE EventAttendance (
    event_id UUID PRIMARY KEY,
    date DATE NOT NULL,
    attendance_count INT NOT NULL
);

CREATE TABLE FestivalParticipants (
    participant_id UUID PRIMARY KEY,
    event_id UUID REFERENCES EventAttendance(event_id),
    participant_name VARCHAR(50) NOT NULL,
    registration_date TIMESTAMP NOT NULL
);

CREATE TABLE MarketingData (
    data_id UUID PRIMARY KEY,
    event_id UUID REFERENCES EventAttendance(event_id),
    marketing_channel VARCHAR(50) NOT NULL,
    conversion_rate DECIMAL(5,2) NOT NULL
);

CREATE TABLE FestivalTickets (
    ticket_id UUID PRIMARY KEY,
    event_id UUID REFERENCES EventAttendance(event_id),
    ticket_type VARCHAR(50) NOT NULL,
    ticket_price DECIMAL(8,2) NOT NULL
);

CREATE TABLE FestivalFeedback (
    feedback_id UUID PRIMARY KEY,
    event_id UUID REFERENCES EventAttendance(event_id),
    feedback_text TEXT NOT NULL,
    satisfaction_rating INT NOT NULL
);
