tables.table_schema:
CREATE TABLE festival_stakeholders (
    stakeholder_id INT PRIMARY KEY,
    name VARCHAR(100),
    role VARCHAR(50),
    contact_number VARCHAR(15)
);

CREATE TABLE festival_tickets (
    ticket_id INT PRIMARY KEY,
    ticket_type VARCHAR(50),
    price DECIMAL(8, 2),
    availability INT
);

CREATE TABLE transportation_schedule (
    transportation_id INT PRIMARY KEY,
    mode_of_transportation VARCHAR(255),
    departure_time TIMESTAMP,
    arrival_time TIMESTAMP
);

CREATE TABLE accommodation_details (
    accommodation_id INT PRIMARY KEY,
    accommodation_type VARCHAR(255),
    check_in_date DATE,
    check_out_date DATE
);

CREATE TABLE festival_participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(255),
    participant_type VARCHAR(255)
);

CREATE TABLE festival_stages (
    stage_id INT PRIMARY KEY,
    stage_name VARCHAR(255),
    stage_capacity INT
);

CREATE TABLE festival_suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    supplier_type VARCHAR(255)
);
