tables.table_schema:
CREATE TABLE plants (
  plant_id SERIAL PRIMARY KEY,
  scientific_name VARCHAR(100) NOT NULL,
  common_name VARCHAR(100),
  family_name VARCHAR(100) NOT NULL
);

CREATE TABLE genes (
  gene_id SERIAL PRIMARY KEY,
  gene_name VARCHAR(100) NOT NULL,
  gene_sequence TEXT NOT NULL,
  plant_id INT REFERENCES plants(plant_id)
);

CREATE TABLE mutations (
  mutation_id SERIAL PRIMARY KEY,
  mutation_type VARCHAR(50) NOT NULL,
  mutation_details TEXT,
  gene_id INT REFERENCES genes(gene_id)
);

CREATE TABLE PlantSpecies (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50) NOT NULL,
    species_description TEXT
);

CREATE TABLE GeneSequences (
    gene_id INT PRIMARY KEY,
    species_id INT,
    gene_sequence TEXT,
    FOREIGN KEY (species_id) REFERENCES PlantSpecies(species_id)
);

CREATE TABLE ResearchStudies (
    study_id INT PRIMARY KEY,
    study_name VARCHAR(100) NOT NULL,
    study_description TEXT
);

CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    researcher_email VARCHAR(100) UNIQUE
);
