tables.table_schema:
CREATE TABLE genetically_modified_crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    growth_stage VARCHAR(50),
    development_stage ENUM('germination', 'seedling', 'vegetative', 'flowering', 'fruiting', 'ripening')
);

CREATE TABLE CropDevelopmentStages (
    id SERIAL PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    growth_stage VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE CropGrowthData (
    id SERIAL PRIMARY KEY,
    crop_id INT REFERENCES CropDevelopmentStages(id) NOT NULL,
    growth_date DATE NOT NULL,
    growth_height DECIMAL(5,2) NOT NULL
);

CREATE TABLE gm_crops (
    crop_id INT PRIMARY KEY,
    crop_name TEXT,
    growth_stage INT,
    modified_gene TEXT
);

CREATE TABLE growth_stages (
    stage_id INT PRIMARY KEY,
    stage_name TEXT,
    description TEXT
);

CREATE TABLE crop_per_stage (
    crop_id INT,
    stage_id INT,
    FOREIGN KEY (crop_id) REFERENCES gm_crops (crop_id),
    FOREIGN KEY (stage_id) REFERENCES growth_stages (stage_id)
);

CREATE TABLE Crop_Species (
  id INT PRIMARY KEY,
  species_name VARCHAR(50) NOT NULL,
  growth_stage VARCHAR(20) NOT NULL
);

CREATE TABLE Growth_Stage_Log (
  id INT PRIMARY KEY,
  crop_id INT NOT NULL,
  growth_stage VARCHAR(20) NOT NULL,
  growth_date DATE NOT NULL
);

CREATE TABLE Plant_Measurements (
  id INT PRIMARY KEY,
  crop_id INT NOT NULL,
  height_cm DECIMAL(10,2) NOT NULL,
  leaf_width_cm DECIMAL(10,2) NOT NULL
);

CREATE TABLE Genetic_Modifications (
  id INT PRIMARY KEY,
  modification_type VARCHAR(50) NOT NULL,
  modification_date DATE NOT NULL
);

CREATE TABLE Crop_History (
  id INT PRIMARY KEY,
  crop_id INT NOT NULL,
  event_description TEXT NOT NULL,
  event_date DATE NOT NULL
);
