tables.table_schema:
CREATE TABLE gut_microbiome_data ( 
  sample_id UUID PRIMARY KEY, 
  bacteria_name VARCHAR(255), 
  bacteria_count INT 
);

CREATE TABLE research_participants ( 
  participant_id UUID PRIMARY KEY, 
  age INT, 
  gender VARCHAR(10), 
  location VARCHAR(50) 
);

CREATE TABLE bacteria (
    id UUID PRIMARY KEY,
    species TEXT,
    genus TEXT,
    abundance FLOAT
);

CREATE TABLE samples (
    sample_id UUID PRIMARY KEY,
    collection_date DATE,
    sample_location TEXT,
    patient_id UUID
);

CREATE TABLE research (
    research_id UUID PRIMARY KEY,
    researcher_name TEXT,
    research_topic TEXT,
    publication_date DATE
);

CREATE TABLE bacteria (
    bacteria_id INT PRIMARY KEY,
    bacteria_name TEXT,
    bacteria_species TEXT,
    detection_method TEXT
);
