tables.table_schema:
CREATE TABLE regulatory_compliance_audit_trails_pharmaceutical_research (
    id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    research_topic VARCHAR(100),
    audit_trail TEXT
);

CREATE TABLE regulatory_compliance_documents (
    doc_id INT PRIMARY KEY,
    document_name VARCHAR(100),
    document_type VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE regulatory_compliance (
  id INT PRIMARY KEY,
  research_id INT NOT NULL,
  regulations TEXT,
  compliance_status BOOLEAN
);

CREATE TABLE pharmaceutical_research (
  research_id INT PRIMARY KEY,
  research_name TEXT,
  researcher_name TEXT,
  research_status TEXT
);

CREATE TABLE study_results (
    id SERIAL PRIMARY KEY,
    study_id INT NOT NULL,
    result_text TEXT,
    result_date DATE
);
