tables.table_schema:
CREATE TABLE disease_statistics (
	disease_name VARCHAR(100) PRIMARY KEY,
	total_cases INT,
	average_age_diagnosis FLOAT,
	prevalence_rate FLOAT
);

CREATE TABLE TestResults (
    id INT PRIMARY KEY,
    patient_id INT,
    test_name VARCHAR(50),
    result BOOLEAN
);

CREATE TABLE Diseases (
    disease_id INT PRIMARY KEY,
    disease_name VARCHAR(50),
    description TEXT
);

CREATE TABLE PatientDiseases (
    pd_id INT PRIMARY KEY,
    patient_id INT,
    disease_id INT,
    diagnosis_date DATE
);
