tables.table_schema:
CREATE TABLE SoilNutrientLevels (
  id SERIAL PRIMARY KEY,
  location VARCHAR(100),
  nitrogen_level DECIMAL,
  phosphorus_level DECIMAL
);

CREATE TABLE CropYieldData (
  id SERIAL PRIMARY KEY,
  crop_type VARCHAR(100),
  yield DECIMAL,
  soil_id INT REFERENCES SoilNutrientLevels(id)
);

CREATE TABLE SoilNutrientLevels (
    soil_id SERIAL PRIMARY KEY,
    nitrogen_level DECIMAL,
    phosphorus_level DECIMAL,
    potassium_level DECIMAL
);

CREATE TABLE CropYieldData (
    crop_id SERIAL PRIMARY KEY,
    crop_name TEXT,
    yield_amount DECIMAL,
    season TEXT
);

CREATE TABLE FieldData (
    field_id SERIAL PRIMARY KEY,
    field_name TEXT,
    area DECIMAL,
    location TEXT
);
