tables.table_schema:
CREATE TABLE monitoring_locations (
  location_id SERIAL PRIMARY KEY,
  location_name TEXT,
  latitude FLOAT,
  longitude FLOAT
);

CREATE TABLE location (
  id INT PRIMARY KEY,
  location_name VARCHAR(50),
  latitude DECIMAL(9, 6),
  longitude DECIMAL(9, 6)
);

CREATE TABLE pH_measurements (
  id UUID PRIMARY KEY,
  measurement_value DECIMAL,
  measurement_time TIMESTAMP
);

CREATE TABLE temperature_measurements (
  id UUID PRIMARY KEY,
  measurement_value DECIMAL,
  measurement_time TIMESTAMP
);

CREATE TABLE dissolved_oxygen_measurements (
  id UUID PRIMARY KEY,
  measurement_value DECIMAL,
  measurement_time TIMESTAMP
);

CREATE TABLE location_data (
  id UUID PRIMARY KEY,
  latitude DECIMAL,
  longitude DECIMAL
);

CREATE TABLE monitoring_station (
  id UUID PRIMARY KEY,
  station_name VARCHAR(50),
  location_id UUID,
  FOREIGN KEY (location_id) REFERENCES location_data(id)
);
