tables.table_schema:
CREATE TABLE bioremediation_microorganisms ( 
    organism_id INT PRIMARY KEY, 
    organism_name VARCHAR(50) NOT NULL, 
    growth_rate FLOAT NOT NULL, 
    survival_rate FLOAT NOT NULL 
);

CREATE TABLE microorganisms (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    growth_rate DECIMAL,
    survival_rate DECIMAL
);

CREATE TABLE microorganisms (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    growth_rate FLOAT,
    survival_rate FLOAT
);

CREATE TABLE experiments (
    id INT PRIMARY KEY,
    experiment_date DATE,
    temperature FLOAT,
    ph_level FLOAT
);

CREATE TABLE locations (
    id INT PRIMARY KEY,
    location_name VARCHAR(255),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE microorganism_survival_rates (
  organism_id INT PRIMARY KEY,
  organism_name VARCHAR(50),
  growth_rate DECIMAL(5,2),
  survival_rate DECIMAL(5,2)
);

CREATE TABLE project_details (
  project_id INT PRIMARY KEY,
  project_name VARCHAR(100),
  start_date DATE,
  end_date DATE
);

CREATE TABLE research_team (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(50),
  project_id INT REFERENCES project_details(project_id),
  lead_researcher VARCHAR(100)
);
