tables.table_schema:
CREATE TABLE bacteria_genomes (
    genome_id UUID PRIMARY KEY,
    species_name TEXT,
    sequence TEXT
);

CREATE TABLE analysis_results (
    result_id UUID PRIMARY KEY,
    genome_id UUID REFERENCES bacteria_genomes(genome_id),
    analysis_date TIMESTAMP,
    result_data JSON
);

CREATE TABLE bacteria_genomes (
   genome_id VARCHAR(255) PRIMARY KEY,
   species VARCHAR(255),
   sequence_data TEXT
);

CREATE TABLE experiments (
   experiment_id VARCHAR(255) PRIMARY KEY,
   bacteria_id VARCHAR(255),
   experiment_date DATE,
   results TEXT
);

CREATE TABLE research_projects (
   project_id VARCHAR(255) PRIMARY KEY,
   project_name VARCHAR(255),
   start_date DATE,
   end_date DATE
);

CREATE TABLE bacteria_genomes(
    genome_id INT PRIMARY KEY,
    species_name TEXT,
    genome_sequence TEXT
);

CREATE TABLE bacterial_genes(
    gene_id INT PRIMARY KEY,
    gene_name TEXT,
    gene_sequence TEXT,
    genome_id INT,
    FOREIGN KEY (genome_id) REFERENCES bacteria_genomes(genome_id)
);

CREATE TABLE gene_functions(
    function_id INT PRIMARY KEY,
    function_name TEXT
);

CREATE TABLE gene_annotations(
    annotation_id INT PRIMARY KEY,
    gene_id INT,
    function_id INT,
    FOREIGN KEY (gene_id) REFERENCES bacterial_genes(gene_id),
    FOREIGN KEY (function_id) REFERENCES gene_functions(function_id)
);

CREATE TABLE gene_variations(
    variation_id INT PRIMARY KEY,
    gene_id INT,
    variation_type TEXT,
    variation_description TEXT,
    FOREIGN KEY (gene_id) REFERENCES bacterial_genes(gene_id)
);
