tables.table_schema:
CREATE TABLE biofuel_production (
  id INT PRIMARY KEY,
  process_name TEXT NOT NULL,
  energy_consumption FLOAT NOT NULL,
  waste_production FLOAT NOT NULL
);

CREATE TABLE EnergyConsumption (
    id SERIAL PRIMARY KEY,
    process_id INT NOT NULL,
    consumption FLOAT NOT NULL,
    date DATE NOT NULL
);

CREATE TABLE biofuel_process (
    process_id INT PRIMARY KEY,
    process_name VARCHAR(255),
    energy_consumption DECIMAL,
    waste_production DECIMAL
);

CREATE TABLE research_team (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(255),
    researcher_name VARCHAR(255),
    email_address VARCHAR(255)
);

CREATE TABLE EnergyConsumption (
    production_id INT PRIMARY KEY,
    energy_source VARCHAR(50),
    energy_consumed DECIMAL,
    energy_unit VARCHAR(10)
);

CREATE TABLE WasteProduction (
    production_id INT PRIMARY KEY,
    waste_type VARCHAR(50),
    waste_amount DECIMAL,
    waste_unit VARCHAR(10)
);

CREATE TABLE BiofuelProduction (
    production_id INT PRIMARY KEY,
    biofuel_type VARCHAR(50),
    production_amount DECIMAL,
    production_unit VARCHAR(10)
);

CREATE TABLE SustainabilityAnalysis (
    analysis_id INT PRIMARY KEY,
    production_id INT,
    analysis_date DATE,
    sustainability_score DECIMAL
);

CREATE TABLE energy_consumption (
    id INT PRIMARY KEY,
    process_name TEXT,
    energy_type TEXT,
    consumption_amount DECIMAL
);

CREATE TABLE waste_production (
    id INT PRIMARY KEY,
