tables.table_schema:
CREATE TABLE plant_data (
  plant_id INT PRIMARY KEY,
  plant_name VARCHAR(50),
  growth_rate FLOAT,
  pollutant_removal_rate FLOAT
);

CREATE TABLE PollutantRemovalRates (
  project_id INT PRIMARY KEY,
  pollutant_id INT,
  removal_rate FLOAT
);

CREATE TABLE plant_growth_data ( 
    plant_id INT PRIMARY KEY, 
    plant_name VARCHAR(50), 
    growth_rate FLOAT, 
    date_recorded DATE 
);

CREATE TABLE pollutant_removal_rates ( 
    removal_id INT PRIMARY KEY, 
    plant_id INT, 
    pollutant_type VARCHAR(50), 
    removal_rate FLOAT 
);

CREATE TABLE project_success ( 
    project_id INT PRIMARY KEY, 
    project_name VARCHAR(100), 
    success_score INT, 
    project_start_date DATE 
);

CREATE TABLE PlantSpecies (
    species_id INT PRIMARY KEY,
    common_name VARCHAR(50),
    scientific_name VARCHAR(100)
);

CREATE TABLE ProjectParticipants (
    participant_id INT PRIMARY KEY,
    project_id INT,
    participant_name VARCHAR(100),
    role VARCHAR(50)
);
