tables.table_schema:
CREATE TABLE bioplastic_materials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(255) NOT NULL,
    production_method VARCHAR(255) NOT NULL,
    environmental_impact_score FLOAT
);

CREATE TABLE production_facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    certification_status BOOLEAN
);

CREATE TABLE bioplastic_production_processes (
    process_id INT PRIMARY KEY,
    process_name VARCHAR(255),
    process_description TEXT
);

CREATE TABLE lifecycle_assessment (
    assessment_id INT PRIMARY KEY,
    data_id INT,
    assessment_date DATE,
    assessment_result TEXT
);

CREATE TABLE bioplastic_materials (
  material_id INT PRIMARY KEY,
  material_name TEXT NOT NULL,
  production_method TEXT NOT NULL,
  impact_score DECIMAL
);

CREATE TABLE production_sites (
  site_id INT PRIMARY KEY,
  site_name TEXT NOT NULL,
  location TEXT NOT NULL,
  capacity DECIMAL
);

CREATE TABLE manufacturing_processes (
  process_id INT PRIMARY KEY,
  process_name TEXT NOT NULL,
  duration DECIMAL,
  energy_consumption DECIMAL
);

CREATE TABLE impact_categories (
  category_id INT PRIMARY KEY,
  category_name TEXT NOT NULL
);
