tables.table_schema:
CREATE TABLE weather_data (
  date DATE,
  temperature FLOAT,
  precipitation FLOAT,
  humidity FLOAT
);

CREATE TABLE planting_schedule (
  plant_name VARCHAR(50),
  optimal_temperature_range VARCHAR(20),
  optimal_humidity_range VARCHAR(20),
  watering_schedule VARCHAR(100)
);

CREATE TABLE planting_schedule (
    plant_id INT,
    season VARCHAR(50),
    optimal_temperature_range VARCHAR(20),
    optimal_humidity_range VARCHAR(20)
);

CREATE TABLE WeatherData (
  weather_id INT PRIMARY KEY,
  date DATE,
  temperature FLOAT,
  humidity FLOAT
);

CREATE TABLE PlantingHistory (
  history_id INT PRIMARY KEY,
  plant_name VARCHAR(50),
  planting_date DATE,
  harvest_date DATE
);

CREATE TABLE WeatherForecast (
  forecast_id INT PRIMARY KEY,
  date DATE,
  predicted_temperature FLOAT,
  predicted_humidity FLOAT
);

CREATE TABLE SoilMoisture (
  moisture_id INT PRIMARY KEY,
  date DATE,
  moisture_level FLOAT
);
