tables.table_schema:
CREATE TABLE yeast_strains (
    id SERIAL PRIMARY KEY,
    strain_name VARCHAR(100),
    optimal_temperature FLOAT,
    optimal_pH FLOAT
);

CREATE TABLE fermentation_chambers (
    id SERIAL PRIMARY KEY,
    chamber_name VARCHAR(100),
    capacity INTEGER,
    temperature_controlled BOOLEAN
);

CREATE TABLE fermentation_vessel (
    vessel_id INT PRIMARY KEY,
    capacity INT,
    material VARCHAR,
    status VARCHAR
);

CREATE TABLE yeast_strain (
    strain_id INT PRIMARY KEY,
    name VARCHAR,
    origin VARCHAR,
    supplier VARCHAR
);

CREATE TABLE process_logs (
    log_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    description TEXT,
    user_id INT
);

CREATE TABLE EquipmentInventory (
  equipment_id INT PRIMARY KEY,
  equipment_name VARCHAR(255),
  quantity INT
);

CREATE TABLE ProcessOptimization (
  optimization_id INT PRIMARY KEY,
  fermentation_id INT,
  optimization_description TEXT,
  FOREIGN KEY (fermentation_id) REFERENCES FermentationProcess(fermentation_id)
);
